mapper.select:
SELECT *
FROM Employee e
INNER JOIN Department d ON e.dept_id = d.dept_id
WHERE emp_id = #{empId}
AND emp_pass = #{empPass}

mapper.update:
UPDATE Employee
SET
	 emp_pass = #{empPass}
	,emp_name = #{empName}
	,gender = #{gender}
	,address = #{address}
	,birthday = #{birthday}
	,authority = #{authority}
	,dept_id = #{deptId}
WHERE
	emp_id = #{empId}
